function otros_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0, $TemplateDV = '', $TemplateDVP = '') {
	// function to return an editable form for a table records
	// and fill it with data of record whose ID is $selected_id. If $selected_id
	// is empty, an empty form is shown, with only an 'Add New'
	// button displayed.

	global $Translation;

	// mm: get table permissions
	$arrPerm = getTablePermissions('otros');
	if(!$arrPerm['insert'] && $selected_id=='') return $Translation['tableAccessDenied'];
	$AllowInsert = ($arrPerm['insert'] ? true : false);
	// print preview?
	$dvprint = false;
	if($selected_id && $_REQUEST['dvprint_x'] != '') {
		$dvprint = true;
	}

	$filterer_barrio = thisOr($_REQUEST['filterer_barrio'], '');
	$filterer_regimen = thisOr($_REQUEST['filterer_regimen'], '');
	$filterer_eapb = thisOr($_REQUEST['filterer_eapb'], '');

	// populate filterers, starting from children to grand-parents
	if($filterer_eapb && !$filterer_regimen) $filterer_regimen = sqlValue("select regimen from eps where razonsocial='" . makeSafe($filterer_eapb) . "'");

	// unique random identifier
	$rnd1 = ($dvprint ? rand(1000000, 9999999) : '');
	// combobox: fecha
	$combo_fecha = new DateCombo;
	$combo_fecha->DateFormat = "dmy";
	$combo_fecha->MinYear = 1900;
	$combo_fecha->MaxYear = 2100;
	$combo_fecha->DefaultDate = parseMySQLDate('<%%creationDate%%>', '<%%creationDate%%>');
	$combo_fecha->MonthNames = $Translation['month names'];
	$combo_fecha->NamePrefix = 'fecha';
	// combobox: asunto
	$combo_asunto = new Combo;
	$combo_asunto->ListType = 0;
	$combo_asunto->MultipleSeparator = ', ';
	$combo_asunto->ListBoxHeight = 10;
	$combo_asunto->RadiosPerLine = 1;
	if(is_file(dirname(__FILE__).'/hooks/otros.asunto.csv')) {
		$asunto_data = addslashes(implode('', @file(dirname(__FILE__).'/hooks/otros.asunto.csv')));
		$combo_asunto->ListItem = explode('||', entitiesToUTF8(convertLegacyOptions($asunto_data)));
