function prass_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('prass');
	if(!$arrPerm['insert']) return false;

	$data = [
		'documento' => Request::val('documento', ''),
		'nombrepaciente' => Request::val('nombrepaciente', ''),
		'regimen' => Request::lookup('regimen', ''),
		'eps' => Request::lookup('eps', ''),
		'direccion' => Request::val('direccion', ''),
		'telefono' => Request::val('telefono', ''),
		'consulta' => Request::val('consulta', ''),
		'fechaconsulta' => Request::dateComponents('fechaconsulta', ''),
		'prueba' => Request::val('prueba', ''),
		'fechaprueba' => Request::dateComponents('fechaprueba', ''),
		'estado' => Request::val('estado', ''),
		'observacion' => br2nl(Request::val('observacion', '')),
	];

	if($data['nombrepaciente'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Nombre del usuario': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['eps'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Eps': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['direccion'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Direccion': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['telefono'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Telefono': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['consulta'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Recibi&#243; Consulta para atenci&#243;n por sospecha o contagio del COVID?': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['prueba'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Se realiz&#243; prueba confirmatoria para el COVID?': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['estado'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Estado': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: prass_before_insert
	if(function_exists('prass_before_insert')) {
		$args = [];
		if(!prass_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('prass', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	update_calc_fields('prass', $recID, calculated_fields()['prass']);

	// hook: prass_after_insert
	if(function_exists('prass_after_insert')) {
		$res = sql("SELECT * FROM `prass` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!prass_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('prass', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) prass_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

function prass_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function prass_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('prass', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: prass_before_delete
	if(function_exists('prass_before_delete')) {
		$args = [];
		if(!prass_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `prass` WHERE `id`='{$selected_id}'", $eo);

	// hook: prass_after_delete
	if(function_exists('prass_after_delete')) {
		$args = [];
		prass_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='prass' AND `pkValue`='{$selected_id}'", $eo);
}

function prass_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('prass', $selected_id, 'edit')) return false;

	$data = [
		'fecha' => parseCode('<%%editingDate%%>', false, true),
		'documento' => Request::val('documento', ''),
		'nombrepaciente' => Request::val('nombrepaciente', ''),
		'regimen' => Request::lookup('regimen', ''),
		'eps' => Request::lookup('eps', ''),
		'direccion' => Request::val('direccion', ''),
