	}

	// mm: save ownership data
	set_record_owner('soportes', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) soportes_copy_children($recID, $_REQUEST['SelectedID']);

