	$eo = ['silentErrors' => true];

	update_calc_fields('soportes', $data['selectedID'], calculated_fields()['soportes']);

	// hook: soportes_after_update
	if(function_exists('soportes_after_update')) {
		$res = sql("SELECT * FROM `soportes` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!soportes_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='soportes' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

