			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#soporte1').replaceWith('<div class=\"form-control-static\" id=\"soporte1\">' + (jQuery('#soporte1').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#soporte1, #soporte1-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#soporte2').replaceWith('<div class=\"form-control-static\" id=\"soporte2\">' + (jQuery('#soporte2').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#soporte2, #soporte2-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#soporte3').replaceWith('<div class=\"form-control-static\" id=\"soporte3\">' + (jQuery('#soporte3').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#soporte3, #soporte3-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#soporte4').replaceWith('<div class=\"form-control-static\" id=\"soporte4\">' + (jQuery('#soporte4').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#soporte4, #soporte4-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(doc)%%>', $combo_doc->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(doc)%%>', $combo_doc->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(doc)%%>', urlencode($combo_doc->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array('doc' => array('pqr', 'Documento'), );
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !$_REQUEST['Embedded']*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(doc)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(soporte1)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="soporte1" id="soporte1" data-filetypes="jpg|jpeg|gif|png|pdf|doc|docx|txt|mp4|mp3" data-maxsize="2560000" accept=".jpg,.jpeg,.gif,.png,.pdf,.doc,.docx,.txt,.mp4,.mp3">'), $templateCode);
	if($AllowUpdate && $row['soporte1'] != '') {
		$templateCode = str_replace('<%%REMOVEFILE(soporte1)%%>', '<br><input type="checkbox" name="soporte1_remove" id="soporte1_remove" value="1"> <label for="soporte1_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	} else {
		$templateCode = str_replace('<%%REMOVEFILE(soporte1)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(soporte2)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="soporte2" id="soporte2" data-filetypes="jpg|jpeg|gif|png|pdf|doc|docx|txt|mp4|mp3" data-maxsize="2560000" accept=".jpg,.jpeg,.gif,.png,.pdf,.doc,.docx,.txt,.mp4,.mp3">'), $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(soporte3)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="soporte3" id="soporte3" data-filetypes="jpg|jpeg|gif|png|pdf|doc|docx|txt|mp4|mp3" data-maxsize="2560000" accept=".jpg,.jpeg,.gif,.png,.pdf,.doc,.docx,.txt,.mp4,.mp3">'), $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(soporte4)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="soporte4" id="soporte4" data-filetypes="jpg|jpeg|gif|png|pdf|doc|docx|txt|mp4|mp3" data-maxsize="2560000" accept=".jpg,.jpeg,.gif,.png,.pdf,.doc,.docx,.txt,.mp4,.mp3">'), $templateCode);
	if($AllowUpdate && $row['soporte4'] != '') {
		$templateCode = str_replace('<%%REMOVEFILE(soporte4)%%>', '<br><input type="checkbox" name="soporte4_remove" id="soporte4_remove" value="1"> <label for="soporte4_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	} else {
		$templateCode = str_replace('<%%REMOVEFILE(soporte4)%%>', '', $templateCode);
	}

	// process values
	if($selected_id) {
		$templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		$templateCode = str_replace('<%%VALUE(doc)%%>', safe_html($urow['doc']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(doc)%%>', urlencode($urow['doc']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(soporte1)%%>', safe_html($urow['soporte1']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(soporte1)%%>', html_attr($row['soporte1']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(soporte1)%%>', urlencode($urow['soporte1']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(soporte2)%%>', safe_html($urow['soporte2']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(soporte2)%%>', html_attr($row['soporte2']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(soporte2)%%>', urlencode($urow['soporte2']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(soporte3)%%>', safe_html($urow['soporte3']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(soporte3)%%>', html_attr($row['soporte3']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(soporte3)%%>', urlencode($urow['soporte3']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(soporte4)%%>', safe_html($urow['soporte4']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(soporte4)%%>', html_attr($row['soporte4']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(soporte4)%%>', urlencode($urow['soporte4']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(doc)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(doc)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(soporte1)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(soporte1)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(soporte2)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(soporte2)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(soporte3)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(soporte3)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(soporte4)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(soporte4)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
			$templateCode.="\n\tif(document.getElementById('soporte1Edit')) { document.getElementById('soporte1Edit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('soporte1EditLink')) { document.getElementById('soporte1EditLink').style.display='none'; }";
			$templateCode.="\n\tif(document.getElementById('soporte2Edit')) { document.getElementById('soporte2Edit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('soporte2EditLink')) { document.getElementById('soporte2EditLink').style.display='none'; }";
			$templateCode.="\n\tif(document.getElementById('soporte3Edit')) { document.getElementById('soporte3Edit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('soporte3EditLink')) { document.getElementById('soporte3EditLink').style.display='none'; }";
			$templateCode.="\n\tif(document.getElementById('soporte4Edit')) { document.getElementById('soporte4Edit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('soporte4EditLink')) { document.getElementById('soporte4EditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	if( $_REQUEST['FilterField'][1]=='2' && $_REQUEST['FilterOperator'][1]=='<=>') {
		$templateCode.="\n<input type=hidden name=doc value=\"" . html_attr($_REQUEST['FilterValue'][1]) . "\">\n";
	}

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('soportes');
	if($selected_id) {
		$jdata = get_joined_record('soportes', $selected_id);
		if($jdata === false) $jdata = get_defaults('soportes');
		$rdata = $row;
	}
	$templateCode .= loadView('soportes-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: soportes_dv
	if(function_exists('soportes_dv')) {
		$args=[];
		soportes_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}